sp_widget.client_script:
function($scope,$rootScope,spModal,spUtil){
	// widget controller 
	var c = this;

	$rootScope.$on('selectNote', function(event,data) {
		c.server.get({
			action: 'getEmp',
			noteID: data
		}).then(function(r) {
			c.data.name = r.data.note1.employee_name;
			c.data.location=r.data.note1.location_1;
			c.data.noteID = r.data.note1.sys_id;
			var tempDate = r.data.note1.date_of_joining_1;
			c.data.doj = new Date(tempDate);
		});
	});

	$rootScope.$on('newNote',function(event)
								 {
		c.data.name = '';
		c.data.location = '';
		c.data.doj = '';
		c.data.noteID='';

	})

	c.Submit = function()
	{

		function convert(str) {
			var date = new Date(str),
					mnth = ("0" + (date.getMonth() + 1)).slice(-2),
					day = ("0" + date.getDate()).slice(-2);
			console.log(date);
			return [date.getFullYear(), mnth, day].join("-");
		}
		var date1=convert(c.data.doj);
		c.server.get({
			action: 'newEmp',
			location: c.data.location,
			name: c.data.name,
			doj:date1
		}).then(function(r) {
			c.data.noteID = r.data.noteID;
			$rootScope.noteID = c.data.noteID;
			$rootScope.$emit('shiftNotes',c.data);
			c.data={};
		});
	}

	c.deleteNote = function() {
		c.server.get({
			action: 'deleteNote',
			noteID: c.data.noteID
		}).then(function(r) {
			spUtil.addTrivialMessage("The "+c.data.name+" record has been deleted.");
			$rootScope.$emit('deleteNote', c.data.noteID);
			c.data.name = '';
			c.data.location = '';
			c.data.doj = '';
			c.data.noteID='';
		});
	}
	c.confirmDelete = function(){
		spModal.confirm("Are you sure you want to delete this Note record?").then(function(confirmed){
			if(confirmed){
				c.deleteNote();

			}
		});
	}
}

sp_widget.script:
(function() {

	if (input && input.noteID) {
		var note = new GlideRecord('demo_employee');
		if (note.get(input.noteID))
		{
			if (input.action == 'getEmp') {
				console.log('inside get emp-> '+input.noteID);
				data.note1 = {};
				$sp.getRecordValues(data.note1, note, "employee_name,location_1,date_of_joining_1, sys_id");
			}

			else if (input.action == 'deleteNote') { 
				note.deleteRecord(); 
			}
			else
			{
				console.log('Error');
			}
		}
	}
	else if(input)
	{
		if (input.action == 'newEmp') {
			var newNote = new GlideRecord('demo_employee');
			newNote.initialize(); 
			newNote.user=gs.getUser().getID();
			newNote.employee_name=input.name;
			newNote.location_1=input.location;
			var temp_date=new GlideDateTime(input.doj);
			newNote.date_of_joining_1=temp_date;
			var id = newNote.insert();
			newNote.noteID=id;
			data.noteID=id;
		}
	}

})();
