sp_widget.script:
(function() {
	//create an array to populate with notes
	data.notes = [];
	var noteGR = new GlideRecord('demo_employee');
	noteGR.addQuery('user', gs.getUser().getID());
	//noteGR.orderByDesc('sys_created_on');
	noteGR.query();

	while (noteGR.next()) {
		var noteObj = {};
		$sp.getRecordDisplayValues(noteObj, noteGR, 'location,employee name,sys_id');
		noteObj.location = noteGR.getValue('location_1');
		noteObj.name = noteGR.getValue('employee_name')
		console.log(noteObj);
		//push the populated obj into the array
		data.notes.push(noteObj);
	}
	if (input) {
		if (input.action == 'newEmp') {
			var newNote = new GlideRecord('demo_employee');
			newNote.newRecord();
			var id = newNote.insert();
			data.noteID = id;
			data.newNote = {};
			data.user = gs.getuser()
			$sp.getRecordValues(data.newNote,newNote,"title,note,sys_id");

		}
	}

})();
